        $criteria->addJoin(BarangPeer::ID_PRODUSEN, ProdusenPeer::ID, $join_behavior);

        // symfony_behaviors behavior
        foreach (sfMixer::getCallables(self::getMixerPreSelectHook(__FUNCTION__)) as $sf_hook)
        {
          call_user_func($sf_hook, 'BaseBarangPeer', $criteria, $con);
        }


        $stmt = BasePeer::doSelect($criteria, $con);
        $results = array();

        while ($row = $stmt->fetch(PDO::FETCH_NUM)) {
            $key1 = BarangPeer::getPrimaryKeyHashFromRow($row, 0);
            if (null !== ($obj1 = BarangPeer::getInstanceFromPool($key1))) {
                // We no longer rehydrate the object, since this can cause data loss.
                // See http://www.propelorm.org/ticket/509
                // $obj1->hydrate($row, 0, true); // rehydrate
            } else {
                $cls = BarangPeer::getOMClass();

                $obj1 = new $cls();
                $obj1->hydrate($row);
                BarangPeer::addInstanceToPool($obj1, $key1);
            } // if obj1 already loaded

                // Add objects for joined Kategori rows

                $key2 = KategoriPeer::getPrimaryKeyHashFromRow($row, $startcol2);
                if ($key2 !== null) {
                    $obj2 = KategoriPeer::getInstanceFromPool($key2);
                    if (!$obj2) {

                        $cls = KategoriPeer::getOMClass();

                    $obj2 = new $cls();
                    $obj2->hydrate($row, $startcol2);
                    KategoriPeer::addInstanceToPool($obj2, $key2);
                } // if $obj2 already loaded

                // Add the $obj1 (Barang) to the collection in $obj2 (Kategori)
                $obj2->addBarang($obj1);

            } // if joined row is not null

                // Add objects for joined Produsen rows

                $key3 = ProdusenPeer::getPrimaryKeyHashFromRow($row, $startcol3);
                if ($key3 !== null) {
                    $obj3 = ProdusenPeer::getInstanceFromPool($key3);
                    if (!$obj3) {

                        $cls = ProdusenPeer::getOMClass();

                    $obj3 = new $cls();
                    $obj3->hydrate($row, $startcol3);
                    ProdusenPeer::addInstanceToPool($obj3, $key3);
                } // if $obj3 already loaded

                // Add the $obj1 (Barang) to the collection in $obj3 (Produsen)
                $obj3->addBarang($obj1);

            } // if joined row is not null

            $results[] = $obj1;
        }
        $stmt->closeCursor();

        return $results;
    }


